import React, { useEffect } from 'react';
import io from 'socket.io-client';
import auth from '../../../../service/authService';
import Chatbox from './Chatbox';
import { useField } from '../../../../hooks/useField';

let socket;

const Chat = ({ channel }) => {
  // define emit message event
  const message = useField('text');

  const sendMessage = (e) => {
    e.preventDefault();
    console.log(message.value);
    socket.emit('message', message.value, (data) => {
      console.log(data);
    });
  };

  useEffect(() => {
    auth.login({
      email: 'email@gmail.com',
      password: 'password',
    });

    const authorization = window.localStorage.getItem('authorization');
    // do it in initial render cause then it will connect before that...
    socket = io('http://localhost:8001/channel');

    // join that channel (room)
    socket.emit('join channel',
      { authorization, channel });

    socket.on('connect', () => {
      // from servers
      socket.on('server message', (data) => {
        console.log(data);
      });

      socket.emit('message', 'client connected', () => {
        console.log('sent message to server');
      });
    });

    return () => {
      socket.off();
    };
  }, [channel]);

  return (
    <>
      <Chatbox
        message={message}
        sendMessage={sendMessage}
      />
    </>
  );
};

export default Chat;
