import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from 'react-router-dom';
import io from 'socket.io-client';
import auth from './service/authService';
import channelService from './service/channelService';

const socket = io('http://localhost:8001/');

const App = () => {

  const [state, setState] = useState({
    selectedChannel: '',
    channelStates: {},
  });

  useEffect(() => {
    socket.on('connect', () => {
      // from servers
      socket.on('server message', (data) => {
        console.log(data);
      });

      const joinChannelsObj = {
        authorization: window.localStorage.getItem('authorization'),
        username: window.localStorage.getItem('username'),
      };
      console.log(joinChannelsObj);
      socket.emit('join channels', joinChannelsObj, (data) => {
        console.log(data);
      });
    });
  }, []);

  // handle initial state
  useEffect(() => {
    // temp login / set user
    auth.login({
      email: 'email@gmail.com',
      password: 'password',
    });

    // grab all channel data, messages
    channelService
      .getUserData()
      .then(({ channelData }) => {
        const channelStates = channelData.reduce((obj, ele) => {
          console.log(obj)
          if (!obj[ele.data.channel]) {
            // eslint-disable-next-line no-param-reassign
            obj[ele.data.channel] = {
              users: ele.data.users,
              name: ele.data.name,
              messages: ele.messages,
            };
          }
          return obj;
        }, {});
        setState(channelStates);
      });
  }, []);


  const sendMessage = (e, message, channelID) => {
    e.preventDefault();
    const messageObj = {
      message,
      channelID,
      authorization: window.localStorage.getItem('authorization'),
      username: window.localStorage.getItem('username'),
    };
    socket.emit('message', messageObj, (data) => {
    });
  };

  return (
    <React.Suspense fallback={<div>Loading...</div>}>
    </React.Suspense>
  );
};

export default App;
