  channel, users, name, messages, emitSendMessage, emitDeleteMessage,
}) => {
  const message = useField('text');

  const sendMessage = (e) => {
    e.preventDefault();
    emitSendMessage(message.value, channel);
    message.reset();
  };

  const deleteMessage = (e) => {
    e.preventDefault();
    emitDeleteMessage(messageID, channel);
  };

  return (
    <div>
      {name}
      <Chat
        message={message}
        messages={messages}
        sendMessage={sendMessage}
      />
    </div>
  );
};

export default Channel;
