import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from 'react-router-dom';
import io from 'socket.io-client';
import auth from './service/authService';
import channelService from './service/channelService';

const Channel = React.lazy(() => import('./components/Channel/Channel'));

const socket = io('http://localhost:8001/');

const App = () => {
  const [channels, setChannelData] = useState([]);
  const [messages, setMessages] = useState({});

  useEffect(() => {
    auth.login({
      email: 'email@gmail.com',
      password: 'password',
    });

    channelService
      .getMessages()
      .then(({ channelData }) => {
        console.log(channelData);
        setChannelData([...channelData]);
      });
    console.log(socket);
    socket.on('connect', () => {
      // from servers
      socket.on('server message', (data) => {
        console.log(data);
      });

      const joinChannelsObj = {
        authorization: window.localStorage.getItem('authorization'),
        username: window.localStorage.getItem('username'),
      };
      console.log(joinChannelsObj);
      socket.emit('join channels', joinChannelsObj, (data) => {
        console.log(data);
      });
    });
  }, []);

  const sendMessage = (e, message, channelID) => {
    e.preventDefault();
    const messageObj = {
      message,
      channelID,
      authorization: window.localStorage.getItem('authorization'),
      username: window.localStorage.getItem('username'),
    };
    socket.emit('message', messageObj, (data) => {
      setMessages([...messages, data]);
    });
  };

  // use the channels state
  const channelViews = channels.map(({ data, messages }) => {
    const { channel, name, users } = data;
    console.log('channel id:', channel);
    console.log('channel name:', name);
    console.log('users:', users);

    return (
      <Route key={channel} path={`/channel/${channel}`}>
        <Channel
          socket={socket}
          channel={channel}
          channelName={name}
          initialMessages={messages}
          sendMessage={sendMessage}
        />
      </Route>
    );
  });

  const channelList = channels.map(({ data }) => {
    const { name, channel } = data;
    return (
      <li key={channel}>
        <Link to={`/channel/${channel}`}>{name}</Link>
      </li>
    );
  });

  return (
    <React.Suspense fallback={<div>Loading...</div>}>
      {/* <Router>
        <nav>
          <ul>
            {channelList}
          </ul>
        </nav>
        <Switch>
          {channelViews}
        </Switch>
      </Router> */}
    </React.Suspense>
  );
};

export default App;
