/* eslint-disable react/prop-types */
import React, { useState } from 'react';
import ReactEmoji from 'react-emoji';

const Message = ({
  id, username, message, created, emitDeleteMessage,
}) => {
  // implement delete, edit, emoji
  const [confirm, setConfirm] = useState(false);
  const [edit, setEdit] = useState(false);

  const confirmAction = () => {
    // setConfirm(false);
    console.log('delete it');
    setConfirm(false);
    setEdit(false);
  };

  const cancelAction = () => {
    setConfirm(false);
    setEdit(false);
  };

  return (
    <section>
      <article>
        <span readOnly={!edit}>
          {`${username}: ${ReactEmoji.emojify(message)} <created: ${created}>`}
        </span>
        { confirm || edit ? (
          <div>
            <button type="button" onClick={confirmAction}> confirm </button>
            <button type="button" onClick={cancelAction}> cancel </button>
          </div>
        ) : (
          <div>
            <button type="button" onClick={() => setConfirm(true)}> delete </button>
            <button type="button" onClick={() => setEdit(true)}> edit </button>
            <button type="button"> emoji </button>
          </div>
        )}
      </article>
    </section>
  );
};

export default Message;
