import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import auth from '../../../../service/authService';
import Chatbox from './Chatbox';
import { useField } from '../../../../hooks/useField';
import TextContainer from './TextContainer';

let socket;

const Chat = ({ channelID }) => {
  // define emit message event
  const message = useField('text');
  const [messages, setMessages] = useState([]);
  const sendMessage = (e) => {
    e.preventDefault();
    console.log(message.value);
    socket.emit('message', message.value, (data) => {
      console.log(data);
    });
  };

  useEffect(() => {
    auth.login({
      email: 'email@gmail.com',
      password: 'password',
    });

    const authorization = window.localStorage.getItem('authorization');
    const username = window.localStorage.getItem('username');
    // do it in initial render cause then it will connect before that...
    socket = io('http://localhost:8001/channel');

    socket.on('connect', () => {
      // from servers
      socket.on('server message', (data) => {
        console.log(data);
      });
      // join that channel (room)
      socket.emit('join channel',
        {
          authorization,
          channelID,
          username,
        });
    });
  }, [channelID]);

  // handles messaging and message changes
  useEffect(() => {
    socket.on('message', (messageObj) => {
      console.log(messageObj);
      setMessages([...messages, messageObj]);
    });

    return () => {
      socket.off();
    };
  }, [messages]);

  return (
    <>
      <TextContainer messages={messages} />
      <Chatbox
        message={message}
        sendMessage={sendMessage}
      />
    </>
  );
};

export default Chat;
