import React, { useState, useEffect } from 'react';

const Chat = React.lazy(() => import('./Container/Chat/Chat'));

const Channel = ({ socket, channel, channelName, initialMessages }) => {
  const [messages, setMessages] = useState([]);
  useEffect(() => {
    setMessages((prev) => prev.concat(initialMessages));
  }, [initialMessages]);

  return (
    <React.Suspense fallback={<div>Loading...</div>}>
      {channelName}
      <Chat
        key={channel}
        channelID={channel}
        socket={socket}
        messages={messages}
        setMessages={setMessages}
      />
    </React.Suspense>
  );
};

export default Channel;
