import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import Chatbox from './Chatbox';
import { useField } from '../../../../hooks/useField';
import TextContainer from './TextContainer';

const Chat = ({ channelID, initialMessages, socket }) => {
  // define emit message event
  const message = useField('text');
  const [messages, setMessages] = useState([]);

  const sendMessage = (e) => {
    e.preventDefault();
    console.log(message.value);
    socket.emit('message', message.value, (data) => {
      console.log(data);
    });
  };
  useEffect(() => {
    // do it in initial render cause then it will connect before that...
    console.log(channelID);
    socket.on('connect', () => {
      // from servers
      socket.on('server message', (data) => {
        console.log(data);
      });
      // join that channel (room)
      const joinChannelObj = {
        authorization: window.localStorage.getItem('authorization'),
        channelID,
        username: window.localStorage.getItem('username'),
      };
      console.log(joinChannelObj);
      socket.emit('join channel', joinChannelObj);
    });
  }, [channelID]);

  // handles messaging and message changes
  useEffect(() => {
    socket.on('message', (messageObj) => {
      console.log(messageObj);
      setMessages([...messages, messageObj]);
    });

    return () => {
      socket.off();
    };
  }, [initialMessages, messages]);

  return (
    <>
      <TextContainer messages={messages} />
      <Chatbox
        message={message}
        sendMessage={sendMessage}
      />
    </>
  );
};

export default Chat;
