import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from 'react-router-dom';
import io from 'socket.io-client';
import auth from './service/authService';
import channelService from './service/channelService';

const Channel = React.lazy(() => import('./components/Channel/Channel'));

const socket = io('http://localhost:8001/');

const App = () => {
  const [channels, setChannels] = useState([]);
  const [channelMembers, setChannelMembers] = useState([]);
  useEffect(() => {
    auth.login({
      email: 'email@gmail.com',
      password: 'password',
    });

    channelService
      .getMessages()
      .then(({ members, messages }) => {
        console.log('messages:', messages);
        console.log('members:', members);
        setChannels([...messages]);
        setChannelMembers([...members]);
      });
    console.log(socket);
    socket.on('connect', () => {
      // from servers
      socket.on('server message', (data) => {
        console.log(data);
      });

      const joinChannelsObj = {
        authorization: window.localStorage.getItem('authorization'),
        username: window.localStorage.getItem('username'),
      };
      console.log(joinChannelsObj)
      socket.emit('join channels', joinChannelsObj, (data) => {
        console.log(data);
      });
    });
  }, []);


  console.log(channelMembers);
  const channelViews = channels.map(({ channel, messages }) => {
    return (
      <Route path={`/channel/${channel}`}>
        <Channel
          key={channel}
          socket={socket}
          channel={channel}
          messages={messages}
          members={setChannelMembers}
        />
      </Route>
    );
  });

  const channelList = channels.map(({ channel }) => {
    return (
      <li>
        <Link to={`/channel/${channel}`}>{channel}</Link>
      </li>
    )
  });

  return (
    <React.Suspense fallback={<div>Loading...</div>}>
      <Router>
        <nav>
          <ul>
            {channelList}
          </ul>
        </nav>
        <Switch>
          {channelViews}
        </Switch>

      </Router>
    </React.Suspense>
  );
};

export default App;
