/* eslint-disable react/prop-types */
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Input from './Input';
import TextContainer from './TextContainer';

import useField from '../../../../hooks/useField';

const Chatbox = ({ channel }) => {
  const message = useField('text');
  const [messages, setMessages] = useState([]);

  const sendMessage = (e) => {
    e.preventDefault();
    console.log(message);
    axios.post('http://localhost:8001/api/channel/message', { message, channel: '5e39ef67458c2b44ab410e57' });
  };

  useEffect(() => {
    const channelMessages = db.filter((msgs) => msgs.channel_id === channel.id);
    if (channelMessages[0]) {
      setMessages(channelMessages[0].messages);
    }
  }, [channel]);

  return (
    <div>
      <TextContainer
        messages={messages}
      />
      <Input
        message={message}
        sendMessage={sendMessage}
      />
    </div>
  );
};

export default Chatbox;
